mapper.select:
SELECT NOTICE_NO, NOTICE_TITLE, NOTICE_REG_DT,EMP_NO
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					SELECT NOTICE_NO, NOTICE_TITLE, NOTICE_REG_DT,EMP_NO
					FROM NOTICE 
					ORDER BY NOTICE_NO DESC 
					) A
			)
WHERE RN BETWEEN #{start} AND #{end}

SELECT NOTICE_NO, NOTICE_TITLE, NOTICE_REG_DT,EMP_NO
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					SELECT NOTICE_NO, NOTICE_TITLE, NOTICE_REG_DT,EMP_NO,NOTICE_CHK
					FROM NOTICE 
					ORDER BY NOTICE_NO DESC 
					) A
			)
WHERE RN BETWEEN #{start} AND #{end} AND NOTICE_CHK = #{notice_chk}

mapper.update:
UPDATE NOTICE 
	SET NOTICE_TITLE = #{notice_title}, NOTICE_CONTENT = #{notice_content}
	WHERE NOTICE_NO = #{notice_no}

mapper.select:
      SELECT NOTICE_NO, NOTICE_TITLE, NOTICE_REG_DT,EMP_NO
			FROM (
				SELECT ROWNUM RN, A.* 
					FROM (
							SELECT NOTICE_NO, NOTICE_TITLE, NOTICE_REG_DT,EMP_NO
							FROM NOTICE 
							ORDER BY NOTICE_NO DESC 
							) A
					)
		WHERE RN BETWEEN #{start} AND #{last}
